mapper.select:
SELECT id, nsrsbh shxydm, nsrmc, nsrlx, tdbh, dh, tdsyqzbh, tdmc, tdsyqrnsrsbh, tdsyqrmc, tdxz, tdqdfs, tdyt, jdxz, zgswskfj, tdzldz, zytdmj, qzqdtdsyqzfje, qztdkfcb, csqdsj, '' nsywzzsj, yxqq, yxqz, zspm, tddji tddj, sebz, jmstdmj, yjmsje FROM t_db_swj_tdsyxx t
AND SUBSTR(t.yxqq, 1, 4) <= #{qdrq} AND SUBSTR(t.yxqz, 1, 4) >= #{qdrq}
and nsrmc = #{nsrmc}
order by tdbh, yxqq, yxqz

SELECT id, t.nsrsbh, t.nsrmc, t.dzsphm, t.pzhm, t.zsxm, t.zspm, t.skssqq, t.skssqz, t.hy, t.zsdlfs, t.jsyj, t.kssl, t.sl, t.sjje, t.rkrq, t.yskm, t.ysfpbl, t.skssswjg, t.zgswskfj, t.pzzl, t.skzl, t.sbfs, t.zsfs, t.jdxz, t.djxh FROM t_db_swj_jrkxx t WHERE ZSPM NOT IN ('滞纳金','罚款') AND ZSXM = '城镇土地使用税' AND tzlx IN ('未被调账的税款', '调账后产生的新税款')
AND SUBSTR(t.SKSSQQ,1,4)=#{qdrq}
and t.nsrmc = #{nsrmc}
