mapper.select:
select t.*, IFNULL(d.dict_label,t.zsxm) as zsxmmc from t_srfxxt_czsrncjhwh t left join sys_dict_data d on t.zsxm=d.dict_value and d.dict_type='dm_yskm'

	 and zsxm = #{zsxm}
	 and ssnd = #{ssnd}

order by d.dict_sort

mapper.insert:
	INSERT INTO t_srfxxt_czsrncjhwh ( 
		ZSXM,QKJ, YBYS, SSND, CREATE_BY, CREATE_TIME
    ) VALUES ( 
        #{zsxm},#{qkj}, #{ybys}, #{ssnd}, #{createBy}, #{createTime}
    )
